import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { Provider } from "react-redux";
import store from "./store";
import jwt_decode from "jwt-decode";
import setAuthToken from "./utils/setAuthToken";
import { setCurrentUser, logoutUser } from "./actions/authActions";

import Ninja from './Ninja';
import Old from './Old'; 
import AddNinja from './AddNinja';
import Register from './components/auth/Register';
import Login from './components/auth/Login';import PrivateRoute from "./components/private-route/PrivateRoute";
import Dashboard from "./components/dashboard/Dashboard";

// Check for token to keep user logged in
if (localStorage.jwtToken) {
  // Set auth token header auth
  const token = localStorage.jwtToken;
  setAuthToken(token);
  // Decode token and get user info and exp
  const decoded = jwt_decode(token);
  // Set user and isAuthenticated
  store.dispatch(setCurrentUser(decoded));
// Check for expired token
  const currentTime = Date.now() / 1000; // to get in milliseconds
  if (decoded.exp < currentTime) {
    // Logout user
    store.dispatch(logoutUser());
    // Redirect to login
    window.location.href = "./login";
  }
}

class App extends Component {
  state = {

    ninjas: [
      {name: 'Hany', age:30, belt: 'black', id:1},
      {name: 'Razan', age:20, belt: 'black', id:2},
      {name: 'Tia', age:25, belt: 'black', id:3}
    ]
  }
  addNinja =(ninja) =>{
      console.log(ninja);
      ninja.id= Math.random()*10;
      let ninjas2 = [...this.state.ninjas, ninja]    
      this.setState({
        ninjas: ninjas2
      })
  }
  deleteNinja =(id) =>{
    console.log(id);
    let ninjas = this.state.ninjas.filter(ninja => {
      return ninja.id!== id
    })
    this.setState({
      ninjas: ninjas
    })
  }
  render() {
    return (
      <Provider store={store}>
      <Router>
      <div className="App">
      <Route exact path="/register" component={Register} />
          <Route exact path="/login" component={Login} />
          <Switch>
              <PrivateRoute exact path="/dashboard" component={Dashboard} />
            </Switch>
        <h1>My First React Application</h1>
        <p>Welcome :)</p>
        <Ninja deleteNinja={this.deleteNinja} ninjas={this.state.ninjas}/> 
        <AddNinja addNinja={this.addNinja}/>
        <Old />
        </div>
        </Router>
        </Provider>

    );
  }
}

export default App;
