import React, { Component } from 'react';
import Ninja from './Ninja';
import Old from './Old'; 
import AddNinja from './AddNinja';
import Landing from './component/layout/Landings'

class App extends Component {
  state = {

    ninjas: [
      {name: 'Hany', age:30, belt: 'black', id:1},
      {name: 'Razan', age:20, belt: 'black', id:2},
      {name: 'Tia', age:25, belt: 'black', id:3}
    ]
  }
  addNinja =(ninja) =>{
      console.log(ninja);
      ninja.id= Math.random()*10;
      let ninjas2 = [...this.state.ninjas, ninja]    
      this.setState({
        ninjas: ninjas2
      })
  }
  deleteNinja =(id) =>{
    console.log(id);
    let ninjas = this.state.ninjas.filter(ninja => {
      return ninja.id!== id
    })
    this.setState({
      ninjas: ninjas
    })
  }
  render() {
    return (
      <div className="App">
        <h1>My first React app</h1>
        <p>Welcome :)</p>
        <Ninja deleteNinja={this.deleteNinja} ninjas={this.state.ninjas}/> 
        <AddNinja addNinja={this.addNinja}/>
        <Old />
        <Landing/>
    </div>
    );
  }
}

export default App;
